package basics;

/*
 * For area of circle 
 * 
 * @version 1.0
 */

public class Area {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		declaration
		double rad=7.5, area;
		final double pi=3.1459;
		
//		area of circle
		area = pi*rad*rad;
		
//		print the area
		System.out.println("Area is " + area);

	}

}
