public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		
		while(i<=10) {
			System.out.println(i+ ".James");
			i++;
		}
			System.out.println("Exiting loop");
			
			System.out.println("********************************************************************************");
			int j=10;
			while(j>=1) {
				System.out.print(j+ "\t");
				j--;
			}
				System.out.println("Exiting loop");
	
	}

}
